mapper.select:
SELECT T1.`index`, T1.`corpCd`, T1.`depaCd`, T1.`depaNm`, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.inpId) AS inpId, T1.`inpDttm`, T1.`updDttm`, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.updId) AS updId, T1.`title`, T1.`content`, T1.`hit`, T2.saveNm AS thumbSaveNm, T2.ext AS thumbExt, T2.origNm AS thumbOrigNm, T2.size AS thumbSize, T2.saveRoot AS thumbSaveRoot, T3.saveNm AS videoSaveNm, T3.ext AS videoExt, T3.origNm AS videoOrigNm, CAST(AES_DECRYPT(UNHEX(T3.saveRoot), #{secretKey}) AS CHAR) as videoSaveRoot FROM tb_archive T1 LEFT JOIN tb_archive_thumbnail T2 ON T1.index = T2.archiveIndex LEFT JOIN tb_archive_video T3 ON T1.index = T3.archiveIndex WHERE T1.corpCd = #{corpCd}
AND T1.title like '%' #{title} '%'
AND SUBSTRING(T1.depaCd, 3) = #{depaDivi}
ORDER BY `index` DESC;

SELECT T1.`index`, T1.`archiveIndex`, T1.`corpCd`, T1.`saveNm`, T1.`origNm`, CAST(AES_DECRYPT(UNHEX(T1.saveRoot), #{secretKey}) AS CHAR) as saveRoot, T1.`ext`, T1.`size` FROM tb_archive_video T1 LEFT JOIN tb_archive T2 ON T1.archiveIndex = T2.`index` WHERE T1.`corpCd` = #{corpCd}
AND T1.`archiveIndex` = #{archiveIndex}
ORDER BY `index`;

SELECT T1.`index`, T1.`archiveIndex`, T1.`corpCd`, T1.`saveNm`, T1.`origNm`, CAST(AES_DECRYPT(UNHEX(T1.saveRoot), #{secretKey}) AS CHAR) as saveRoot, T1.`ext`, T1.`size` FROM tb_archive_thumbnail T1 LEFT JOIN tb_archive T2 ON T1.archiveIndex = T2.`index` WHERE T1.`corpCd` = #{corpCd}
AND T1.`archiveIndex` = #{archiveIndex}
ORDER BY `index`;

SELECT T1.`index`, T1.`archiveIndex`, T1.`corpCd`, T1.`saveNm`, T1.`origNm`, CAST(AES_DECRYPT(UNHEX(T1.saveRoot), #{secretKey}) AS CHAR) as saveRoot, T1.`ext`, T1.`size` FROM tb_archive_file T1 LEFT JOIN tb_archive T2 ON T1.archiveIndex = T2.`index` WHERE T1.`corpCd` = #{corpCd}
AND T1.`archiveIndex` = #{archiveIndex}
ORDER BY `index`;

mapper.update:
UPDATE tb_archive T1
SET T1.hit = T1.hit + 1
WHERE T1.`corpCd` = #{corpCd}
  AND T1.`index` = #{archiveIndex}

UPDATE tb_archive_thumbnail
SET updId    = #{userId},
    updDttm  = now(),
    saveNm   = #{saveNm},
    origNm   = #{origNm},
    saveRoot = HEX(AES_ENCRYPT(#{saveRoot}, #{secretKey})),
    ext      = #{ext},
    size     = #{size}
WHERE corpCd = #{corpCd}
  AND `archiveIndex` = #{archiveIndex}

mapper.delete:
DELETE
FROM tb_archive_thumbnail
WHERE `corpCd` = #{corpCd}
  AND `busiCd` = #{busiCd}
  AND `index` = #{index}
