mapper.select:
SELECT `index`, `corpCd`, `depaCd`, `depaNm`, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.inpId) AS inpId, `inpDttm`, `updDttm`, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.updId) AS updId, `title`, `content`, `hit` FROM tb_erp_notice T1 WHERE corpCd = #{corpCd}
AND DATE(inpDttm) BETWEEN #{stDt} AND #{edDt}
AND title like '%' #{title} '%'
ORDER BY `index` DESC;

SELECT T1.`index`, T1.`noticeIndex`, T1.`corpCd`, T1.`saveNm`, T1.`origNm`, CAST(AES_DECRYPT(UNHEX(saveRoot), #{secretKey}) AS CHAR) as saveRoot, T1.`ext`, T1.`size`, T1.`inpId`, T1.`inpDttm`, T1.`empNm` # T2.`year` FROM tb_erp_notice_file T1 LEFT JOIN tb_erp_notice T2 ON T1.noticeIndex = T2.`index` WHERE T1.`corpCd` = #{corpCd}
AND T1.`noticeIndex` = #{noticeIndex}
ORDER BY `index`;

SELECT T1.`index`, T1.`corpCd`, T1.`depaCd`, T1.`inpDttm`, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.inpId) AS inpId, (SELECT empNm FROM TB_BASE_USER_LIST I1 WHERE I1.corpCd = #{corpCd} AND I1.empCd = T1.updId) AS updId, T1.`updDttm`, T1.`title`, T1.`content`, T1.`endDt` FROM tb_erp_notice T1 WHERE T1.corpCd = #{corpCd}
AND T1.`index` = #{index}
AND DATE_FORMAT(#{date}, '%Y-%m-%d') <= T1.endDt
# AND T1.`index` = #{index} ORDER BY `index` DESC;

mapper.update:
UPDATE tb_erp_notice T1
SET T1.hit = T1.hit + 1
WHERE T1.`corpCd` = #{corpCd}
  AND T1.`index` = #{index}

UPDATE tb_erp_notice T1
SET T1.title     = #{title},
    T1.content   = #{content},
    T1.endDt     = #{endDt},
    T1.`updDttm` = now(),
    T1.`updId`   = #{userId}
WHERE T1.`corpCd` = #{corpCd}
  AND T1.`index` = #{noticeIndex}

mapper.delete:
DELETE
FROM tb_erp_notice_image
WHERE `corpCd` = #{corpCd}
  AND `busiCd` = #{busiCd}
  AND `noticeIndex` = #{noticeIndex}
  AND `saveNm` = #{saveNm}

DELETE
FROM tb_erp_notice_file
WHERE `corpCd` = #{corpCd}
  AND `busiCd` = #{busiCd}
  AND `noticeIndex` = #{noticeIndex}
  AND `index` = #{index}

DELETE
FROM tb_erp_notice_file
WHERE `corpCd` = #{corpCd}
  AND `busiCd` = #{busiCd}
  AND `noticeIndex` = #{noticeIndex}
